document.addEventListener("DOMContentLoaded",()=>{document.getElementById("form-sorteador").addEventListener("submit",e=>{e.preventDefault();e=document.getElementById("numero-maximo").value,e=parseInt(e);let t=Math.random()*e;t=(t=Math.round(t))<1?1:t,(resultado=document.getElementById("resultado")).style.display="block",resultado.scrollIntoView(),document.getElementById("resultado-valor").innerText=t})});